#!/usr/bin/env python3

signal = ''
# signal = 'zdrrgvvntvtzzssgcgqqbvqqzmqmrrprjjpmpwpvpqpwppfqqnvvjbj'

def main():
    buff = []
    for ch in signal:
        buff.append(ch)
        # print(buff)
        if len(buff) > 3 :
             if len(set(buff[-4:])) == 4 :
                 print('size of buffer:', len(buff))
                 break

if __name__ == '__main__':
    main()
